import React, { useEffect, useState } from 'react';
import { Text, View, FlatList, TouchableOpacity } from 'react-native';
import estilos from './estilos';
import { pegaRepositrioDoUsuario } from '../../servicos/requisicoes/repositorios';

export default function Repositorios({ route, navigation }) {
    const [repo, setRepo] = useState([]);

    useEffect(async()=>{
        const resultado = await pegaRepositorioDoUsuario(route.params.id)
        setRepo(resultado)
    },[])

    return (
        <View style={estilos.container}>
                <Text style={estilos.repositoriosTexto}>{repo.length} repositórios criados</Text>
                <TouchableOpacity 
                    style={estilos.botao}
                    onPress={() => navigation.navigate('CriarRepositorio')}
                >
                    <Text style={estilos.textoBotao}>Adicionar novo repositório</Text>
                </TouchableOpacity>

            <FlatList 
            data={repo}
            style={{width:'100%'}}
            keyExtractor={repo => repo.id}
            renderItem={({item})=>(
                <TouchableOpacity
                style={estilos.repositorio}
                >
                        <Text style={estilos.repositorioNome}>{item.name}</Text>
                        <Text style={estilos.repositorioData}>Atualizado em {item.data}</Text>

                </TouchableOpacity>
            )}
            />

        </View>
    );
}
